// src/pages/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css'; // pakai CSS yang sudah dibuat

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // dummy login
    if(email === "user@example.com" && password === "123456"){
      navigate('/dashboard'); // navigasi ke dashboard
    } else {
      alert("Email atau password salah!");
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="app-title">EcoLife</h1>
        <p className="subtitle">Login untuk memulai perjalanan hijaumu ðŸŒ¿</p>

        <div className="form-group">
          <label>Email</label>
          <input type="email" value={email} onChange={(e)=>setEmail(e.target.value)} placeholder="Masukkan email" />
        </div>

        <div className="form-group">
          <label>Password</label>
          <input type="password" value={password} onChange={(e)=>setPassword(e.target.value)} placeholder="Masukkan password" />
        </div>

        <button className="login-btn" onClick={handleLogin}>Login</button>
      </div>
    </div>
  );
}
