import { Link } from "react-router-dom";

export default function Register() {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-8 bg-white">
      <h1 className="text-2xl font-semibold mb-4">Create an account</h1>

      <input className="border w-full p-3 rounded mb-3" placeholder="Name" />
      <input className="border w-full p-3 rounded mb-3" placeholder="Email" />
      <input className="border w-full p-3 rounded mb-3" placeholder="Password" />
      <input className="border w-full p-3 rounded mb-3" placeholder="Confirm Password" />

      <button className="bg-black text-white w-full p-3 rounded mt-2">
        Sign in
      </button>

      <p className="mt-4 text-sm">
        Already have an account?{" "}
        <Link to="/" className="text-blue-600">
          Login
        </Link>
      </p>
    </div>
  );
}
