import { Link } from 'react-router-dom';
import './MainDashboard.css';

export default function MainDashboard() {
  return (
    <div className="dashboard-container">
      {/* Profile + Achievement */}
      <section className="profile-section">
        <h2>Halo, User ðŸŒ¿</h2>
        <p>Achievement kamu: <span className="achievement">ðŸŒ± 120 points</span></p>
      </section>

      {/* Tips Section */}
      <section className="tips-section">
        <h3>Tips Hijau Terbaru</h3>
        <ul>
          <li><Link to="/tips/1">Kurangi penggunaan plastik sekali pakai</Link></li>
          <li><Link to="/tips/2">Hemat energi listrik di rumah</Link></li>
          <li><Link to="/tips/3">Tanam pohon di lingkungan sekitar</Link></li>
        </ul>
      </section>

      {/* QR Code Section */}
      <section className="qr-section">
        <h3>Scan QR Code</h3>
        <Link to="/qr-scanner">
          <button className="qr-btn">Buka QR Scanner</button>
        </Link>
      </section>
    </div>
  );
}
