// src/pages/QRScanner.jsx
import React, { useState } from 'react';
import Header from '../components/Header';
import QRCode from 'qrcode.react';
import './QRScanner.css';
import { useNavigate } from 'react-router-dom';

export default function QRScanner() {
  const navigate = useNavigate();
  const [scanned, setScanned] = useState(false);

  // Dummy QR data (bisa unique per session/user)
  const qrData = "ecolife-login-session-12345";

  const handleSimulateScan = () => {
    setScanned(true);
    // simulasi login / check-in
    setTimeout(() => {
      navigate('/dashboard'); // redirect ke dashboard setelah scan
    }, 1200);
  };

  return (
    <div className="qr-container">
      <Header username="Karenina" points={120} />

      <div className="scanner-card">
        <h2>QR Code Login / Check-in ðŸŒ¿</h2>
        <p>Scan QR code ini dengan HP untuk login cepat atau check-in</p>

        <div className="scanner-box">
          <QRCode value={qrData} size={180} />
          <div className="scan-line"></div>
        </div>

        {!scanned ? (
          <button className="scan-btn" onClick={handleSimulateScan}>Simulasikan Scan</button>
        ) : (
          <p className="success-msg">Scan berhasil! Mengarahkan ke dashboard...</p>
        )}
      </div>
    </div>
  );
}
